	function commonJump(obj,list){
        var maxView = (GALAXY.sizeMode == 4) ? 6 : 5;
        var lens = list.length;
		var btn = obj.find("a");
        var view = lens - maxView;
        var count = 0;

        obj.find('.prev').addClass('hide');

		btn.off("click");
		btn.on("click",function(){
				$(this).siblings().removeClass('hide');
				if($(this).hasClass("next")){

                    count ++;
                    if(count >= view) $(this).addClass('hide');
                    if(count > view) count = count - 1;
                    $('.m_jump_controls .jump_wrap ul').css('left', -(list.width()*count));

				}else if($(this).hasClass("prev")){

                    count --;
                    if(count <= 0) $(this).addClass('hide');
                    if(count < 0) count = 0;
                    $('.m_jump_controls .jump_wrap ul').css('left', -(list.width()*count));

			}
			return false;
		});
	}
